using AdaptiveCards;
using System;
using System.Collections.Generic;
using System.Linq;
using System.Threading.Tasks;

namespace DotNetBot.Cards
{
    public static class testCard2
    {
        public static AdaptiveCard Create(string bodyVariable)
        {
            AdaptiveCard card = new AdaptiveCard("1.2");

            card.BackgroundImage = new AdaptiveBackgroundImage("data:image/png;base64,");

            card.Body.Add(new AdaptiveTextBlock()
            {
                Text = $"Here is some body text: " + bodyVariable,
                Size = AdaptiveTextSize.Large,
                Color = AdaptiveTextColor.Dark,
                Wrap = true
            });

            card.Actions.Add(new AdaptiveOpenUrlAction()
            {
                Type = AdaptiveOpenUrlAction.TypeName,
                Title = "Go somewhere",
                Url = new Uri("https://adaptivecards.io/")
            });

            return card;
        }
    }
}
